tween(layer[buttons].y,get(posicion_buttons_new),0.1);

add(posicion_derecha,get(layer[barra_derecha].x),109);
sub(layer[barra_inferior].width,get(stagewidth),get(posicion_derecha));

delayedcall(0.001, if(loaded, acciones_eternas()));
